var Player = {

	extensions : require('extensions.js'),
	id : null,
	ai : false,
	moves : [],
	behavior : null,

	create : function(identifier, isAI) {
		var player = this.extensions.clone(this);
		player.setId(identifier);
		player.setBehavior(isAI);
		return player;
	},	

	setBehavior : function(isAI) {
		if (isAI) {
			this.behavior = {};
			this.behavior.defensive = Math.floor(Math.random() * 6) + 1;
			this.behavior.offensive = 10 - this.behavior.defensive;			
		}
	},

	getBehaviorWeightings : function() {
		if (this.isAI()) {
			return {
				defensive : this.behavior.defensive / 10,
				offensive : this.behavior.offensive / 10
			};
		}
	},

	setId : function(identifier) {
		this.id = identifier;
	},

	getId : function() {
		return this.extensions.clone(this.id);
	},

	getMoves : function() {
		return this.extensions.clone(this.moves);
	},

	addMove : function(move) {
		this.moves.push(move);
	},

	isAI : function () {
		return this.behavior != null;
	},

	export : function () {
		exports.create = this.extensions.bind(this.create, this);
	}	
};

Player.export();