var Player = {

	extensions : require('extensions.js'),
	id : null,
	ai : false,
	moves : [],

	create : function(identifier, isAI) {
		var player = this.extensions.clone(this);
		player.setId(identifier);
		player.isAI(isAI);
		return player;
	},	

	setId : function(identifier) {
		this.id = identifier;
	},

	getId : function() {
		return this.extensions.clone(this.id);
	},

	getMoves : function() {
		return this.extensions.clone(this.moves);
	},

	addMove : function(move) {
		this.moves.push(move);
	},

	isAI : function (isAnAI) {
		this.ai = isAnAI || this.ai;
		return this.ai;
	},

	export : function () {
		exports.create = this.extensions.bind(this.create, this);
	}	
};

Player.export();